<?phpdeclare(strict_types=1);namespace ContaoManager;use Contao\ManagerPlugin\Bundle\Config\ConfigInterface;use Contao\ManagerPlugin\Bundle\Parser\ParserInterface;use MetaModels\AttributeContentArticleBundle\ContaoManager\Plugin;use MetaModels\AttributeContentArticleBundle\MetaModelsAttributeContentArticleBundle;use MetaModels\CoreBundle\MetaModelsCoreBundle;use PHPUnit\Framework\TestCase;/** * @covers \MetaModels\AttributeContentArticleBundle\ContaoManager\Plugin */class PluginTest extends TestCase{    public function testGetBundles(): void    {        $parser = $this->createMock(ParserInterface::class);        $config = $this->createMock(ConfigInterface::class);        $config            ->expects(self::once())            ->method('getName')            ->willReturn(MetaModelsAttributeContentArticleBundle::class);        $config            ->expects(self::once())            ->method('getLoadAfter')            ->willReturn([MetaModelsCoreBundle::class]);        $config            ->expects(self::once())            ->method('getReplace')            ->willReturn(['metamodelsattribute_article']);        $plugin  = new Plugin();        $bundles = $plugin->getBundles($parser);        foreach ($bundles as $bundle) {            self::assertSame($config->getName(), $bundle->getName());            self::assertSame($config->getLoadAfter(), $bundle->getLoadAfter());            self::assertSame($config->getReplace(), $bundle->getReplace());        }    }}