<?phpdeclare(strict_types=1);namespace Widgets;use Contao\Config;use Contao\CoreBundle\Framework\Adapter;use Doctrine\DBAL\Connection;use MetaModels\AttributeContentArticleBundle\Widgets\ContentArticleWidget;use PHPUnit\Framework\TestCase;/** * @covers \MetaModels\AttributeContentArticleBundle\Widgets\ContentArticleWidget */class ContentArticleWidgetTest extends TestCase{    public function testNewInstance(): void    {        $connection = $this->createMock(Connection::class);        $input = $this->getMockBuilder(Adapter::class)            ->disableOriginalConstructor()            ->setMethods(['get'])            ->getMock();        $input            ->expects(self::once())            ->method('get')            ->willReturn(1);        $widget = $this->getMockBuilder(ContentArticleWidget::class)            ->setConstructorArgs([null, $connection, $input])            ->setMethods(['import'])            ->getMock();        $widget            ->expects(self::any())            ->method('import')            ->withConsecutive([Config::class, 'Config']);        self::assertEmpty($widget->getAttributes());    }}